import React, { Component ,forceUpdate} from 'react'

import {observable,action,makeObservable} from 'mobx'
import {Observer} from 'mobx-react'

class MyState{
    @observable num=0

      constructor(){
          makeObservable(this)
      }
      @action.bound addNum = () => {
        // this.num++;
        forceUpdate(()=>this.num++)
        console.log('num',this.num)
      };
}

const newState=new MyState()


@Observer
class Mobx extends Component{
    render(){
        return(
            <div>
                <h1>Mobx</h1>
                <p>{newState.num}</p>
                <p>
                    <button onClick={newState.addNum} >Increment</button>
                </p>
            </div>
        )
    }
}
export default Mobx;
